INSERT INTO products (product_id, category_id, name, description, count, price, hide)
VALUES ('fb190607-5d8b-478a-9541-b3fc377d9968', 1, 'Apple Watch Nike Series 7 45mm', 'Apple Watch Nike с приложением Nike Run Club — мощный партнёр для бега. Их новый циферблат добавляет каждому вашему движению динамики. Аудиопробежки с тренером заряжают мотивацией.Словно на пружинах. Apple Watch Nike созданы для того, чтобы быть в движении. И новый интерактивный циферблат «‎На пружинах» отлично это иллюстрирует. Он находится в постоянной динамике, реагируя на каждое ваше движение. Выберите для него любимые цвета или градиент, чтобы тренировки с Apple Watch стали ещё ярче.', 184, 39299, false);
INSERT INTO products (product_id, category_id, name, description, count, price, hide)
VALUES ('fb290607-5d8b-478a-9541-b3fc377d9968', 1, 'Garmin Venu', 'Смарт-часы Garmin Venu 010-02173-23 с изящным дизайном созданы для пользователей, отдающих предпочтение активному образу жизни. В данной модели используется 1.2-дюймовый AMOLED-экран круглой формы, обрамленный стильной золотистой рамкой. Информация выводится на него в разрешении 390x390 пикселей, ее без труда можно будет разглядеть даже при ярком солнечном свете. Устройство взаимодействует со смартфоном или планшетом, функционирующим на базе операционной системы Android или iOS, посредством приложения Garmin Connect. Прочный корпус из нержавеющей стали соответствует степени защиты WR50, что позволяет эксплуатировать смарт-часы в самых суровых условиях.', 61, 42750, false);
INSERT INTO products (product_id, category_id, name, description, count, price, hide)
VALUES ('fb390607-5d8b-478a-9541-b3fc377d9968', 1, 'Garmin Fenix 6X PRO Solar GPS Titan', 'Спортивные часы Garmin Fenix 6X PRO Solar - брутальная модель с обширнейшим функционалом, которая приглянется любителям активного отдыха. Одной из ключевых особенностей представленного устройства стала встроенная солнечная батарея, благодаря которой ваши путешествия будут длиться неограниченно долго. Часы оснащены 1.4-дюймовым цветным экраном, на котором отображается не только текущее время, но и прочая информация, сбором которой занимается техника.', 78, 93299, false);
INSERT INTO products (product_id, category_id, name, description, count, price, hide)
VALUES ('fb490607-5d8b-478a-9541-b3fc377d9968', 2, 'OLED LG OLED88Z19LA серый', 'Самоподсвечивающиеся пиксели обеспечивают непревзойденное качество изображения и целый ряд возможностей для дизайна, в то время как передовые технологии заставляют восхищаться. Всё, что вы любите в телевизоре. Во всех смыслах. Если вы ищете телевизор, не похожий ни на один другой — ваши поиски завершились. LG OLED — это произведение искусства, кинотеатр с большим экраном, портал в игровые миры и место в первом ряду на крупнейших спортивных мероприятиях. Всё, что вам когда-либо было нужно в телевизоре.', 181, 2499999, false);
INSERT INTO products (product_id, category_id, name, description, count, price, hide)
VALUES ('fb590607-5d8b-478a-9541-b3fc377d9968', 2, 'LED Samsung QE85QN900AUXRU серый', 'Телевизор Samsung оснащен экраном с диагональю 214 см. Модель Samsung QE85QN900AUXRU обладает экраном с разрешением 7680x4320 (8K UltraHD), поддержкой Smart TV, операционной системой Tizen, входом спутниковой антенны, объемным звучанием.', 165, 999999, true);
INSERT INTO products (product_id, category_id, name, description, count, price, hide)
VALUES ('fb690607-5d8b-478a-9541-b3fc377d9968', 2, 'OLED LG OLED77W9 черный', 'OLED-телевизор LG OLED77W9, разработанный специально для монтажа на стене, создает завораживающее впечатление и привносит эстетику в ваше помещение. Это стало возможным только благодаря уникальной передовой технологии LG OLED TV. Процессор α9 2-го поколения, дополненный алгоритмом глубинного обучения ИИ, оптимизирует скорость своей работы и графику, возвещая о начале эры технологий невероятной детализации изображения и адаптивного звучания для полного погружения в виртуальную реальность. ', 88, 999999, false);
INSERT INTO products (product_id, category_id, name, description, count, price, hide)
VALUES ('fb790607-5d8b-478a-9541-b3fc377d9968', 3, 'Samsung Galaxy Z Fold3 512 ГБ зеленый', 'Смартфон Samsung Galaxy Z Fold3 – это компактный и прочный складной смартфон. Благодаря поддержке электронного пера S Pen все возможности Galaxy Note доступны на безграничном 7.6-дюймовом Infinity Flex дисплее. В сложенном виде Galaxy Z Fold3 — это совершенный во всех отношениях смартфон с 6.2-дюймовым экраном. Благодаря режиму Flex, Galaxy Z Fold3 позволяет смотреть видео, созваниваться по видеосвязи и делать многое другое без помощи рук.', 86, 169999, false);
INSERT INTO products (product_id, category_id, name, description, count, price, hide)
VALUES ('fb890607-5d8b-478a-9541-b3fc377d9968', 3, 'Apple iPhone 13 Pro 1000 ГБ серебристый', 'iPhone 13 Pro. Грандиозный апгрейд камер Pro. Режим «Киноэффект» делает из видео настоящее кино. Дисплей Super Retina XDR с технологией ProMotion для более быстрого и плавного взаимодействия. A15 Bionic — самый быстрый чип для iPhone. Прочный корпус и огромный прирост ресурса аккумулятора.', 84, 149999, false);
